package com.ksg.codingstd;

public class InceamentDemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Pre increment operator
		

		int x = 4;
		
		System.out.println("The original value of a: "+x);
		
		int y = ++x;
		
		System.out.println("The value of a:  "+x+" The value of y:  "+y);
		
		 //Post increment
		
		int a = 4;
		
		System.out.println("The original value of a: "+a);
		
		int b = a++;
		
		System.out.println("The value of  a: "+a+"   The value of b: "+b);
		
		
		
	
	
	
	}


}
