package com.ksg.loop;

public class SwapNumberUsingThirdVariableDemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 5;
		
		int b = 6;
		
		int c = 0;
		
		System.out.println("before swaping the value of a: "+a+ " and the value of b: "+b);
		
		c = a;
		a = b;
		b = c;
		
		System.out.println("after swaping the value of a: "+a+" and the value of b: "+b);
		
		
	
		

	}

}
